bool ChangePageProtect(CodeBuffer Buffer)
{
	DWORD OldProtect = 0;
	VirtualProtect(Buffer.pBuffer, Buffer.BufferSize, PAGE_EXECUTE_READWRITE, &OldProtect);
	return TRUE;
}

